<script setup>import {useScrollReveal} from '~/composables/useScrollReveal.js'import {onMounted, computed} from 'vue'import {useI18n} from 'vue-i18n'const {t} = useI18n()const steps = computed(() => [  {    title: t('approach.step1.title'),    description: t('approach.step1.description'),    image: '/images/approach/research.png'  },  {    title: t('approach.step2.title'),    description: t('approach.step2.description'),    image: '/images/approach/design.png'  },  {    title: t('approach.step3.title'),    description: t('approach.step3.description'),    image: '/images/approach/development.png'  },  {    title: t('approach.step4.title'),    description: t('approach.step4.description'),    image: '/images/approach/testing.png'  },  {    title: t('approach.step5.title'),    description: t('approach.step5.description'),    image: '/images/approach/launch.png'  },  {    title: t('approach.step6.title'),    description: t('approach.step6.description'),    image: '/images/approach/optimization.png'  }])const {reveal} = useScrollReveal()onMounted(() => {  reveal('.approach-card', {interval: 150})})</script><template>  <section class="py-24 bg-black text-white cursor-pointer" id="approach">    <div class="max-w-screen-xl mx-auto px-4 md:px-6 text-center">      <h2 class="text-4xl md:text-5xl font-heading font-bold mb-4">        {{ t('approach.title') }}      </h2>      <p class="text-white/70 max-w-2xl mx-auto mb-12">        {{ t('approach.description') }}      </p>      <div class="grid gap-6 md:grid-cols-2 lg:grid-cols-3">        <div            v-for="(step, i) in steps"            :key="i"            class="approach-card relative p-6 rounded-3xl bg-white/5 backdrop-blur-lg border border-white/10            hover:bg-white/10 hover:-translate-y-2 hover:shadow-xl transition-all duration-300 flex flex-col items-center text-center group overflow-hidden"        >          <div class="relative mb-4 w-12 h-12 flex items-center justify-center">            <div                class="absolute inset-0 rounded-full border-2 border-yellow-600 group-hover:bg-yellow-600 transition-all duration-300"            ></div>            <span class="relative z-10 font-bold text-yellow-600 group-hover:text-black transition-colors duration-300">              {{ i + 1 }}            </span>          </div>          <img              :src="step.image"              alt=""              class="w-20 h-20 mb-4 rounded-xl object-contain opacity-60 group-hover:opacity-100 transition-all duration-300 group-hover:scale-110"          />          <h3 class="text-lg font-bold text-white group-hover:text-yellow-600 transition-colors duration-300 mb-2">            {{ step.title }}          </h3>          <p class="text-sm text-white/70">            {{ step.description }}          </p>        </div>      </div>    </div>  </section></template>