<script setup>import {ref} from 'vue'import {useI18n} from 'vue-i18n'import IconTelegram from '~icons/mdi/telegram'import IconGitHub from '~icons/mdi/github'import IconInstagram from '~icons/mdi/instagram'import IconLinkedin from '~icons/mdi/linkedin'const {t} = useI18n()const email = ref('')const loading = ref(false)const success = ref(false)const error = ref(false)const year = new Date().getFullYear()const brandName = 'Koderra'const contactEmail = 'mykola.mud@gmail.com'const socials = [  {href: 'https://t.me/koderra', icon: IconTelegram},  {href: 'https://github.com/koderra', icon: IconGitHub},  {href: 'https://instagram.com/koderra', icon: IconInstagram},  {href: 'https://linkedin.com/company/koderra', icon: IconLinkedin}]const subscribe = async () => {  success.value = false  error.value = false  const valid = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email.value)  if (!valid) {    error.value = true    return  }  loading.value = true  try {    await $fetch('/api/subscribe', {      method: 'POST',      body: {email: email.value}    })    success.value = true    email.value = ''    setTimeout(() => (success.value = false), 4000)  } catch {    error.value = true  }  loading.value = false}</script><template>  <footer class="relative bg-[#0c0c0c] text-white border-t border-white/10 mt-32 overflow-hidden">    <div class="absolute inset-0 opacity-[0.03] bg-[url('/images/grid-dark.svg')] bg-cover pointer-events-none"/>    <div class="relative max-w-screen-xl mx-auto px-4 md:px-6 py-16 grid gap-12 md:grid-cols-12">      <div class="md:col-span-5 space-y-4">        <h2 class="text-3xl font-heading font-extrabold text-brand">          <Logo/>        </h2>        <p class="text-white/70 text-sm leading-relaxed">{{ t('footer.brandDesc') }}</p>        <div class="flex gap-4 pt-2">          <a              v-for="s in socials"              :key="s.href"              :href="s.href"              target="_blank"              class="hover:text-brand-lime transition"          >            <component :is="s.icon" class="w-5 h-5"/>          </a>        </div>      </div>      <div class="md:col-span-4 space-y-4">        <h3 class="text-lg font-semibold text-white">{{ t('footer.subscribeTitle') }}</h3>        <form @submit.prevent="subscribe" class="w-full max-w-sm mt-6">          <div              class="flex items-center bg-white/10 border border-white/20 rounded-full overflow-hidden backdrop-blur-md"          >            <input                v-model="email"                type="email"                :placeholder="t('footer.placeholderEmail')"                class="flex-1 bg-transparent text-white placeholder-white/40 px-4 py-3 text-sm focus:outline-none"            />            <button                type="submit"                :disabled="loading"                class="flex items-center justify-center px-4 py-3 bg-brand-lime text-black font-semibold text-sm transition-all duration-300 hover:bg-brand hover:text-white disabled:opacity-50"            >              <template v-if="!loading">{{ t('footer.submitIcon') }}</template>              <template v-else>                <svg class="animate-spin h-5 w-5" viewBox="0 0 24 24" fill="none">                  <circle                      class="opacity-25"                      cx="12"                      cy="12"                      r="10"                      stroke="currentColor"                      stroke-width="4"                  />                  <path class="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8H4z"/>                </svg>              </template>            </button>          </div>        </form>        <p v-if="success" class="text-green-400 text-sm">{{ t('footer.subscribeSuccess') }}</p>        <p v-if="error" class="text-red-400 text-sm">{{ t('footer.subscribeError') }}</p>      </div>      <div class="md:col-span-3 space-y-4 text-sm">        <div>          <span class="text-white/50">{{ t('footer.contactEmailLabel') }}</span>          <a :href="`mailto:${contactEmail}`" class="hover:underline ml-1">{{ contactEmail }}</a>        </div>        <div>          <span class="text-white/50">{{ t('footer.contactCityLabel') }}</span>          <span class="ml-1">{{ t('footer.contactCity') }}</span>        </div>      </div>    </div>    <div class="border-t border-white/10 mt-12 pt-6 text-center text-xs text-white/30">      Â© {{ year }} Koderra. {{ t('footer.copyright') }}    </div>  </footer></template>