<script setup>import {ref, onMounted} from 'vue'const logoExists = ref(true)onMounted(() => {  fetch('/images/logo.svg')      .then(response => {        if (!response.ok) logoExists.value = false      })      .catch(() => {        logoExists.value = false      })})</script><template>  <NuxtLink to="/" class="block w-fit">    <img        v-if="logoExists"        src="/images/logo.png"        alt="Koderra Logo"        class="h-12 w-auto"    />    <span        v-else        class="text-2xl font-heading font-extrabold uppercase text-white"    >      KODERRA    </span>  </NuxtLink></template>