<script setup>import { ref, onMounted, onBeforeUnmount } from 'vue'import { useNavigationLinks } from '~/composables/useNavigationLinks.js'const isOpen = ref(false)const links = useNavigationLinks()const handleKeydown = e => {  if (e.key === 'Escape') isOpen.value = false}onMounted(() => window.addEventListener('keydown', handleKeydown))onBeforeUnmount(() => window.removeEventListener('keydown', handleKeydown))</script><template>  <div>    <button @click="isOpen = true" class="text-white hover:text-brand text-3xl transition">      ☰    </button>    <Transition name="mobile-menu">      <div v-if="isOpen" class="fixed inset-0 z-50 flex">        <div class="absolute inset-0 bg-black/80" @click="isOpen = false" />        <aside          class="relative ml-auto h-fit w-[100vw] max-w-sm bg-[#111111] text-white flex flex-col px-6 pt-8 pb-12 z-50 shadow-2xl"        >          <div class="flex justify-between items-center mb-10">            <span class="text-2xl font-heading font-bold tracking-tight">Меню</span>            <button @click="isOpen = false" class="text-2xl text-white hover:text-brand transition">              ✕            </button>          </div>          <nav class="flex flex-col gap-6 text-lg font-medium">            <NuxtLink              v-for="link in links"              :key="link.to"              :to="link.to"              @click="isOpen = false"              class="transition-all hover:text-brand hover:pl-2"            >              {{ link.label }}            </NuxtLink>          </nav>        </aside>      </div>    </Transition>  </div></template><style lang="scss">.mobile-menu-enter-active,.mobile-menu-leave-active {  transition: all 0.3s ease;}.mobile-menu-enter-from {  opacity: 0;  transform: translateX(100%);}.mobile-menu-enter-to {  opacity: 1;  transform: translateX(0%);}.mobile-menu-leave-from {  opacity: 1;  transform: translateX(0%);}.mobile-menu-leave-to {  opacity: 0;  transform: translateX(100%);}</style>