<script setup>import { useNavigationLinks } from '~/composables/useNavigationLinks.js'import { useActiveSection } from '~/composables/useActiveSection.js'const links = useNavigationLinks()const sectionIds = ['home', 'works', 'services', 'approach', 'about', 'blog', 'contact']const { activeSection } = useActiveSection(sectionIds)</script><template>  <nav class="flex items-center gap-6 text-sm font-medium">    <NuxtLink      v-for="link in links"      :key="link.to"      :to="link.to"      :class="[        'transition-all duration-300 ease-in-out inline-block',        activeSection === link.to.replace('/#', '')          ? 'text-yellow-500 font-semibold'          : 'text-white hover:text-yellow-500 hover:-translate-y-0.5 hover:scale-105'      ]"    >      {{ link.label }}    </NuxtLink>  </nav></template>