<script setup>import {useI18n} from 'vue-i18n'import {useLocalePath} from '#imports'import {useNavigationLinks} from '~/composables/useNavigationLinks.js'import {useActiveSection} from '~/composables/useActiveSection.js'const {t} = useI18n()const localePath = useLocalePath()const sectionIds = ['home', 'works', 'services', 'approach', 'about', 'blog', 'contact']const {activeSection} = useActiveSection(sectionIds)const navigationLinks = computed(() => useNavigationLinks(t, localePath))</script><template>  <nav class="flex items-center gap-6 text-sm font-medium">    <NuxtLink        v-for="link in navigationLinks"        :key="link.to"        :to="link.to"        :class="[        'transition-all duration-300 ease-in-out inline-block',        activeSection === link.to.replace('/#', '')          ? 'text-yellow-500 font-semibold'          : 'text-white hover:text-yellow-500 hover:-translate-y-0.5 hover:scale-105'      ]"    >      {{ link.label }}    </NuxtLink>  </nav></template>