<script setup>import BaseButton from '~/components/ui/BaseButton.vue'const backgroundImage = '/images/hero-banner.png'const headline = 'Koderra — інженерна студія веб-рішень'const subtext =  'Я проєктую та розробляю кастомні магазини, сайти та автоматизацію для бізнесів, які хочуть швидко, якісно та без зайвих ланцюгів.'const buttons = [  { label: 'Показати проєкти', to: '/#works', variant: 'individual' },  { label: 'Написати мені', to: '/contact', variant: 'secondary' }]const handleClick = btn => {  const to = btn.to  if (to.startsWith('/#')) {    const anchor = to.slice(2)    const el = document.getElementById(anchor)    if (el) {      el.scrollIntoView({ behavior: 'smooth' })    } else {      console.warn(`Element with id="${anchor}" not found`)    }  } else {    navigateTo(to)  }}</script><template>  <section id="home" class="relative h-screen w-full overflow-hidden">    <div class="absolute inset-0">      <img        :src="backgroundImage"        alt="Background"        class="w-full h-full object-cover object-center md:object-right-top"      />      <div class="absolute inset-0 bg-black/30" />    </div>    <div class="absolute bottom-0 left-0 w-full h-100 z-20 pointer-events-none">      <div class="w-full h-full bg-gradient-to-t from-black/80 via-transparent to-transparent" />    </div>    <div      class="relative z-10 h-full max-w-screen-xl mx-auto flex items-end md:items-center px-4 md:px-6 pb-10 md:pb-0"    >      <div class="bg-white/5 rounded-xl p-6 md:p-12 border border-white/10 max-w-2xl w-full">        <h1          class="text-2xl sm:text-3xl md:text-5xl font-heading font-extrabold tracking-tight text-white leading-tight mb-4 md:mb-6"        >          {{ headline }}        </h1>        <p class="text-white/80 text-sm sm:text-base md:text-lg mb-6 md:mb-8 leading-relaxed">          {{ subtext }}        </p>        <div class="flex flex-col sm:flex-row gap-4 flex-wrap">          <BaseButton            v-for="btn in buttons"            :key="btn.to"            :variant="btn.variant"            :to="btn.to.startsWith('/#') ? undefined : btn.to"            @click="handleClick(btn)"          >            {{ btn.label }}          </BaseButton>        </div>      </div>    </div>  </section></template>