<script setup>import {useI18n} from 'vue-i18n'defineProps({service: Object})const {t} = useI18n()</script><template>  <div      class="relative cursor-pointer group bg-gradient-to-br from-white/5 via-white/10 to-white/5 border border-white/10 rounded-3xl p-6 md:p-8 shadow-lg backdrop-blur-xl flex flex-col justify-between min-h-[520px] transition-all duration-300 hover:border-yellow-600 hover:shadow-yellow-400/30 hover:shadow-2xl"      @click="$emit('select', service)"  >    <div class="w-20 h-20 mx-auto mb-4">      <img          :src="service.image3d"          alt=""          class="w-full h-full object-contain pointer-events-none select-none opacity-60 group-hover:opacity-100 transition-opacity duration-300"      />    </div>    <h3        class="text-xl md:text-2xl font-heading font-bold text-white text-center tracking-wider mb-5 uppercase group-hover:text-yellow-600 transition-colors duration-300"    >      {{ service.title }}    </h3>    <div class="text-white/80 text-sm space-y-4 flex-1">      <div          class="flex items-center gap-2 justify-center text-white/60 group-hover:text-yellow-600 transition-colors"      >        <span>⏱</span>        <span>{{ service.duration }}</span>      </div>      <div>        <p class="text-yellow-400 font-semibold mb-2">{{ t('servicesSection.goalsTitle') }}</p>        <ul class="space-y-1">          <li              v-for="(goal, i) in service.goals"              :key="i"              class="flex items-start gap-2 text-white/70 group-hover:text-yellow-200 transition-colors"          >            <span class="text-yellow-400 text-lg leading-none">✦</span>            <span class="leading-snug">{{ goal }}</span>          </li>        </ul>      </div>    </div>    <div class="mt-6 flex items-center justify-between border-t border-white/10 pt-4">      <span class="text-lg font-bold text-white">{{ t('servicesSection.from') }} {{ service.price }}</span>      <span class="text-xs uppercase bg-yellow-400 text-black px-3 py-1 rounded-full font-semibold">        {{ t('servicesSection.orderButton') }}      </span>    </div>  </div></template>