<script setup>import {ref, nextTick, onMounted} from 'vue'import {useI18n} from 'vue-i18n'import {useServices} from '~/composables/useServices'import ServiceCard from '~/components/services/ServiceCard.vue'import OrderModal from '~/components/services/OrderModal.vue'import {useScrollReveal} from '~/composables/useScrollReveal.js'const {t} = useI18n()const services = useServices()const selectedService = ref(null)const orderAnchor = ref(null)const {reveal} = useScrollReveal()const selectService = async service => {  selectedService.value = service  await nextTick()  orderAnchor.value?.scrollIntoView({behavior: 'smooth'})}onMounted(() => {  reveal('.services', {    origin: 'bottom',    duration: 800,    distance: '20px',    scale: 0.95,    interval: 150,    easing: 'ease-in-out'  })})</script><template>  <section ref="orderAnchor" class="services py-2 bg-black text-white relative">    <div class="max-w-screen-xl mx-auto px-4 md:px-6">      <div class="mb-14 justify-center flex flex-col items-center">        <h2 class="text-4xl md:text-5xl font-heading font-bold mb-4 text-center">          {{ t('servicesSection.title') }}        </h2>        <p class="text-white/60 text-base md:text-lg max-w-2xl text-center">          {{ t('servicesSection.description') }}        </p>      </div>      <div class="grid gap-8 sm:grid-cols-2 lg:grid-cols-3 justify-items-center">        <ServiceCard v-for="s in services" :key="s.id" :service="s" @select="selectService"/>      </div>    </div>    <Transition name="fade">      <OrderModal          class="absolute top-[55%]"          v-if="selectedService"          :isOpen="true"          :service="selectedService"          @close="selectedService = null"      />    </Transition>  </section></template><style lang="scss">.fade-enter-active,.fade-leave-active {  transition: opacity 0.3s ease;}.fade-enter-from,.fade-leave-to {  opacity: 0;}</style>