<template>  <NuxtLink      :to="to"      class="inline-flex items-center justify-center text-sm font-medium rounded-full px-6 py-3 transition"      :class="computedClass"  >    <slot/>  </NuxtLink></template><script setup>const props = defineProps({  to: {type: String, required: true},  variant: {    type: String,    default: 'primary',  },})const variantClassMap = {  primary: 'bg-red-500 text-white hover:bg-white/80',  secondary: 'border border-white text-white hover:bg-white/10',}const computedClass = computed(() => variantClassMap[props.variant] || '')</script>