<script setup>import {ref, watch} from 'vue'import {useI18n} from 'vue-i18n'import {useRouter, useRoute} from 'vue-router'import {useLocalePath} from '#imports'const {locale} = useI18n()const router = useRouter()const route = useRoute()const localePath = useLocalePath()const isEnglish = ref(locale.value === 'en')watch(locale, (newVal) => {  isEnglish.value = newVal === 'en'})const toggleLocale = async () => {  const next = isEnglish.value ? 'uk' : 'en'  const newPath = localePath(route.fullPath, next)  await router.push(newPath)}</script><template>  <button      @click="toggleLocale"      class="relative w-16 h-8 rounded-full bg-white/10 border border-white/20 flex items-center px-1 transition-colors"  >    <div        class="absolute inset-0 flex justify-between items-center px-2 text-xs font-bold pointer-events-none"    >      <span>UA</span>      <span>EN</span>    </div>    <div        class="w-6 h-6 rounded-full bg-white text-black flex items-center justify-center text-[10px] font-bold shadow transition-transform"        :class="isEnglish ? 'translate-x-8' : 'translate-x-0'"    >      {{ isEnglish ? 'EN' : 'UA' }}    </div>  </button></template>