<script setup>import {useI18n} from 'vue-i18n'import {useWorks} from '~/composables/useWorks'import {onMounted} from 'vue'import {useScrollReveal} from '~/composables/useScrollReveal.js'const works = useWorks()const {t} = useI18n()const sectionTitle = t('works.title')const sectionDescription = t('works.description')const readMoreLabel = t('works.common.readMore')const {reveal} = useScrollReveal()onMounted(() => {  reveal('.work-card', {    origin: 'bottom',    duration: 800,    distance: '20px',    scale: 0.95,    interval: 150,    easing: 'ease-in-out'  })})</script><template>  <section id="works" class="py-28 scroll-mt-15 bg-black text-white relative overflow-hidden">    <div class="max-w-screen-xl mx-auto px-4 md:px-6">      <div class="mb-20 text-center">        <h2 class="text-4xl md:text-5xl font-heading font-bold">          {{ sectionTitle }}        </h2>        <p class="text-white/60 text-base md:text-lg mt-4 max-w-2xl mx-auto">          {{ sectionDescription }}        </p>      </div>      <div class="grid md:grid-cols-2 gap-12">        <NuxtLink            v-for="work in works"            :key="work.title"            :to="work.url"            class="work-card group relative flex flex-col md:flex-row gap-6 p-6 md:p-8 rounded-3xl backdrop-blur-lg bg-white/5 border border-white/10 hover:bg-white/10 hover:shadow-2xl transition-all duration-300 overflow-hidden"        >          <div              class="absolute inset-0 border border-transparent group-hover:border-brand transition-all duration-300 rounded-3xl pointer-events-none"          ></div>          <div class="w-full md:w-1/3 overflow-hidden rounded-2xl relative">            <img                :src="work.image"                :alt="work.title"                class="w-full h-48 md:h-full object-cover object-center transition-transform duration-500 group-hover:scale-105"            />            <div                v-if="work.badge"                class="absolute text-amber-500 top-3 left-3 bg-brand-lime text-brand-dark text-xs font-bold px-3 py-1 rounded-full"            >              {{ work.badge }}            </div>          </div>          <div class="flex-1 flex flex-col justify-between">            <div>              <div class="flex items-center gap-2 text-sm uppercase text-brand tracking-wide mb-2">                <svg class="w-4 h-4 fill-current" viewBox="0 0 20 20">                  <circle cx="10" cy="10" r="10"/>                </svg>                {{ work.category }}              </div>              <h3                  class="text-2xl font-heading font-bold leading-tight mb-3 group-hover:text-brand transition"              >                {{ work.title }}              </h3>              <p class="text-white/70 text-sm md:text-base leading-relaxed line-clamp-3">                {{ work.description }}              </p>              <div v-if="work.tags?.length" class="mt-4 flex flex-wrap gap-2">                <span                    v-for="tag in work.tags"                    :key="tag"                    class="text-xs bg-white/10 border border-white/20 text-white px-3 py-1 rounded-full"                >                  {{ tag }}                </span>              </div>            </div>            <span class="inline-block mt-6 text-sm text-brand font-medium hover:underline">              {{ readMoreLabel }} â†’            </span>          </div>        </NuxtLink>      </div>    </div>  </section></template>